import * as React from "react";
import {
  Box,
  TextField,
  Typography,
  Avatar,
  Grid,
  Paper,
  CircularProgress, // Import CircularProgress
} from "@mui/material";
import axios from "axios";
import Card from "@mui/material/Card";
import { Divider } from "@mui/material";

function ChatUI() {
  const [input, setInput] = React.useState("");
  const [messages, setMessages] = React.useState([
    { id: 1, text: "Hello! ðŸ˜Š", sender: "bot", timestamp: new Date() },
  ]);
  const [inputDisabled, setInputDisabled] = React.useState(false);
  const [loading, setLoading] = React.useState(false); // Added loading state

  const handleSend = async () => {
    if (input.trim() !== "") {
      setInputDisabled(true);
      setMessages([
        ...messages,
        {
          id: messages.length + 1,
          text: input,
          sender: "user",
          timestamp: new Date(),
        },
      ]);

      const newMessage = {
        id: messages.length + 1,
        text: input,
        sender: "user",
        timestamp: new Date(),
      };

      try {
        setLoading(true); // Start loading

        const response = await axios.post("https://192.168.2.3:9001/entries/", [
          {
            role: "user:",
            content: input,
          },
        ], {
          headers: {
            "content-type": "application/json",
            "Access-Control-Allow-Credentials": "true",
            "Access-Control-Allow-Private-Network": "true",
            "Access-Control-Allow-Origin": "*",
            "Access-Control-Allow-Headers": "*",
          },
        });
        const botResponse = response.data.content;

        setMessages([
          ...messages,
          newMessage,
          {
            id: messages.length + 2,
            text: botResponse,
            sender: "bot",
            timestamp: new Date(),
          },
        ]);
        setInput("");
      } catch (error) {
        console.error("Error sending message:", error);
      } finally {
        setLoading(false); // Stop loading
        setInputDisabled(false);
      }
    }
  };

  const handleInputChange = (event) => {
    setInput(event.target.value);
  };

  return (
    <Grid container spacing={1} justifyContent="center">
      <Grid item xs={12} lg={14}>
        <Card>
          <Box
            p={2}
            sx={{
              height: "90vh",
              display: "flex",
              flexDirection: "column",
              bgcolor: "transparent",
            }}
          >
            <Typography variant="h5">Ecatrom Chat Interface</Typography>
            <Divider />
            <Box sx={{ flexGrow: 1, overflow: "auto", p: 1 }}>
              {messages.map((message) => (
                <Message key={message.id} message={message} />
              ))}
            </Box>
            <Box sx={{ p: 1 }}>
              <Grid container spacing={1} justifyContent="center">
                <Grid item xs={12} lg={14}>
                  <TextField
                    size="medium"
                    fullWidth
                    placeholder="Type a message"
                    variant="outlined"
                    value={input}
                    onChange={handleInputChange}
                    onKeyPress={(event) => {
                      if (event.key === "Enter") {
                        handleSend();
                      }
                    }}
                    disabled={inputDisabled}
                  />
                  {loading && (
                    <CircularProgress sx={{ position: "absolute" }} /> // Display the loading icon
                  )}
                </Grid>
              </Grid>
            </Box>
          </Box>
        </Card>
      </Grid>
    </Grid>
  );
}
const Message = ({ message }) => {
  const isBot = message.sender === "bot";

  return (
    <Box
      sx={{
        display: "flex",
        justifyContent: isBot ? "flex-start" : "flex-end",
        mb: 2,
      }}
    >
      <Box
        sx={{
          display: "flex",
          flexDirection: isBot ? "row" : "row-reverse",
          alignItems: "center",
        }}
      >
        <Avatar sx={{ bgcolor: isBot ? "white" : "grey.300" }}>
          {isBot ? "AI" : "U"}
        </Avatar>
        <Paper
          variant="outlined"
          sx={{
            p: 1,
            ml: isBot ? 1 : 0,
            mr: isBot ? 0 : 1,
            backgroundColor: isBot ? "white" : "grey.300",
            borderRadius: isBot ? "20px 20px 20px 5px" : "20px 20px 5px 20px",
            whiteSpace: "pre-wrap", // Set white-space to pre-wrap
          }}
        >
          <Typography variant="body2">{message.text}</Typography>
          <Typography variant="caption" color="textSecondary">
            {message.timestamp.toLocaleTimeString()}
          </Typography>
        </Paper>
      </Box>
    </Box>
  );
}

export default ChatUI;
